import React from 'react';

class Forms extends React.Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.state = ({
      selectForm: '',
      textForm: '',
      emailForm: '',
      textareaForm: '',
    })
  }

  handleChange({ target }) {
    this.setState({[target.name]: target.value})
  }

  render() {
    return(
      <form>
        <select name='selectForm' value={this.state.selectForm} onChange={this.handleChange}>
          <option>roll20</option>
          <option>rrpg</option>
          <option>outros</option>
        </select>
        <input type='text' name='textForm' value={this.state.textForm} onChange={this.handleChange}></input>
        <input type='email' name='emailForm' value={this.state.emailForm} onChange={this.handleChange}></input>
        <textarea name='textareaForm' value={this.state.textareaForm} onChange={this.handleChange}></textarea>
      </form>
    );
  }
}

export default Forms;
