import React from 'react';
import Select from './Select';

class Forms extends React.Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.state = ({
      selectForm: '',
      textForm: '',
      emailForm: '',
      textareaForm: '',
      checkboxForm: '',
      teste: '',
    })
  }

  handleChange({ target }) {
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({[target.name]: value})
  }

  render() {
    return(
      <form>
        <fieldset>
          <Select value={this.state.selectForm} handleChange={this.handleChange} />
          <input type='text' name='textForm' value={this.state.textForm} onChange={this.handleChange}></input>
          <input type='email' name='emailForm' value={this.state.emailForm} onChange={this.handleChange}></input>
          <textarea name='textareaForm' value={this.state.textareaForm} onChange={this.handleChange}></textarea>
          <input type='checkbox' name='checkboxForm' value={this.state.checkboxForm} onChange={this.handleChange} />
          <input type="file" />
        </fieldset>
      </form>
    );
  }
}

export default Forms;
