import React from 'react';

class Form extends React.Component {
  render() {
    const estados = ['AC', 'AL', 'AP', 'AM', 'BA', 'CE', 'DF', 'ES', 'GO', 'MA', 'MT', 'MS', 'MG', 'PA', 'PB', 'PR', 'PE', 'PI', 'RJ', 'RN', 'RS', 'RO', 'RR', 'SC', 'SP', 'SE', 'TO']
    return(
      <form>
        <fieldset>
          <input id="username" name="username" type="text" maxLength="40" placeholder="Nome" required></input>
          <input name="userEmail" type="email" maxLength="50" placeholder="Email" required></input>
          <input name="CPF" type="text" maxLength="11" placeholder="CPF" required></input>
          <input name="address" type="text" maxLength="200" placeholder="EndereÃ§o" required></input>
          <input name="city" type="text" maxLength="28" placeholder="Cidade" required></input>
          <select name="estado" required>
            {estados.map((estado) => <option>{estado}</option>)}
          </select>
          <label for="casa">
            <input id="casa" name="moradia" type="radio" />Casa
          </label>
          <label for="apartamento">
            <input id="apartamento" name="moradia" type="radio" />Apartamento
          </label>
        </fieldset>
        <fieldset>
          <textarea name="cv" maxLength="1000" required />
          <textarea name="cargo" maxLength="40" required />
          <textarea name="description" maxLength="500" required />
        </fieldset>
      </form>
    );
  }
}

export default Form;
