import React from 'react';
import Form from './Form';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.handleChanges = this.handleChanges.bind(this);

    this.state = {
      username: '',
      email: '',
      CPF: '',
      address: '',
      city: '',
      estado: '',
      cv: '',
      cargo: '',
      description: '',
    }
  }

  handleChanges({ target }) {
    this.setState({
      [target.name]: target.value,
    })
  }

  render() {
    return (
      <Form onInputChange={this.handleChanges}/>
    );
  }
}

export default App;
