import React from 'react';
import Form from './Form';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.handleChanges = this.handleChanges.bind(this);
    this.checkNumber = this.checkNumber.bind(this);
    this.removeCharacters = this.removeCharacters.bind(this);

    this.state = {
      username: '',
      userEmail: '',
      CPF: '',
      address: '',
      city: '',
      estado: '',
      cv: '',
      cargo: '',
      description: '',
      moradia: '',
    }
  }

  handleChanges({ target }) {
    this.setState({
      [target.name]: target.value,
    })
  }

  removeCharacters() {
    const {address} = this.state;
    this.setState({
      address: address.replace(/[^a-zA-Z0-9]/g, ''),
    })
  }

  checkNumber() {
    const {city} = this.state;
    console.log(city);
    if(isNaN(Number(city.charAt(0)))) {
      this.setState({
        city: '',
      })
    }
  }

  render() {
    const { username, userEmail, CPF, address, city, estado, cv, cargo, description } = this.state;
    return (
      <Form
        onInputChange={this.handleChanges}
        username={username}
        userEmail={userEmail}
        CPF={CPF}
        address={address}
        city={city}
        estado={estado}
        cv={cv}
        cargo={cargo}
        description={description}
        keyUp={this.removeCharacters}
        onBlur={this.checkNumber}
      />
    );
  }
}

export default App;
