import React from 'react';

class User extends React.Component {
  render() {
    const { data } = this.props;
    return (
      <div>
        {data.map((user) => user.dob.age) <= 50 ? <><img
          src={data.map((user) => user.picture.medium)}
          alt={data.map((user) => user.name.first)} /><p>{`${data.map((user) => user.name.first)} ${data.map((user) => user.name.last)}`}</p><p>{data.map((user) => user.email)}</p><p>{`Age: ${data.map((user) => user.dob.age)}`}</p></> : ''}
      </div>
    );
  }
}

export default User;
