import React from 'react';

class User extends React.Component {
  render() {
    const { data } = this.props;
    return (
      <div>
        <img
          src={data.map((user) => user.picture.medium)}
          alt={data.map((user) => user.name.first)} 
        />
        <p>{`${data.map((user) => user.name.first)} ${data.map((user) => user.name.last)}`}</p>
        <p>{data.map((user) => user.email)}</p>
      </div>
    );
  }
}

export default User;
